@import url("https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Playfair+Display:wght@400;500;600;700&display=swap");
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(210, 40%, 96%);
  --foreground: hsl(222, 47%, 11%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 47%, 11%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 47%, 11%);
  --primary: hsl(217, 91%, 39%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(158, 64%, 52%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(215, 16%, 47%);
  --accent: hsl(38, 92%, 50%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --ring: hsl(217, 91%, 39%);
  --radius: 0.75rem;
  --font-sans: "Inter", system-ui, sans-serif;
  --font-serif: "Playfair Display", serif;
  --font-mono: "Fira Code", monospace;
}

.dark {
  --background: hsl(222, 47%, 11%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(217, 91%, 59%);
  --primary-foreground: hsl(222, 47%, 11%);
  --secondary: hsl(158, 64%, 52%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(38, 92%, 50%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --ring: hsl(217, 91%, 59%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer components {
  .hero-bg {
    background-image: linear-gradient(to bottom right, hsl(217, 91%, 39% / 0.2), hsl(158, 64%, 52% / 0.3)), 
                      url('https://images.unsplash.com/photo-1602216056096-3b40cc0c9944?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80');
    background-size: cover;
    background-position: center;
  }
}

@layer utilities {
  .animate-fade-in-up {
    animation: fadeInUp 0.8s ease-out forwards;
    opacity: 0;
    transform: translateY(30px);
  }

  .animate-slide-in-left {
    animation: slideInLeft 0.9s ease-out forwards;
    opacity: 0;
    transform: translateX(-50px);
  }

  .animate-slide-in-right {
    animation: slideInRight 0.9s ease-out forwards;
    opacity: 0;
    transform: translateX(50px);
  }

  .animate-zoom-in {
    animation: zoomIn 0.7s ease-out forwards;
    opacity: 0;
    transform: scale(0.8);
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulseGlow 2s ease-in-out infinite;
  }

  .animate-bounce-gentle {
    animation: bounceGentle 2s ease-in-out infinite;
  }

  .animate-rotate-slow {
    animation: rotateSlow 8s linear infinite;
  }

  .delay-100 {
    animation-delay: 0.1s;
  }

  .delay-200 {
    animation-delay: 0.2s;
  }

  .delay-300 {
    animation-delay: 0.3s;
  }

  .delay-400 {
    animation-delay: 0.4s;
  }

  .delay-500 {
    animation-delay: 0.5s;
  }

  .delay-600 {
    animation-delay: 0.6s;
  }

  .delay-700 {
    animation-delay: 0.7s;
  }

  .hover-lift {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-lift:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }

  .gradient-text {
    background: linear-gradient(135deg, #2563eb, #10b981, #f59e0b);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .glass-effect {
    backdrop-filter: blur(20px);
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .animate-slide-up {
    animation: slideUp 0.6s ease-out forwards;
    opacity: 0;
    transform: translateY(40px);
  }

  .animate-scale-in {
    animation: scaleIn 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
    opacity: 0;
    transform: scale(0.9);
  }

  .animate-wiggle {
    animation: wiggle 1s ease-in-out;
  }

  .animate-spin-slow {
    animation: spin 3s linear infinite;
  }

  .hover-glow {
    transition: all 0.3s ease;
  }

  .hover-glow:hover {
    box-shadow: 0 0 30px rgba(59, 130, 246, 0.3);
    transform: translateY(-2px);
  }

  .hover-bounce {
    transition: transform 0.2s ease;
  }

  .hover-bounce:hover {
    transform: scale(1.05) translateY(-5px);
  }

  .hover-slide {
    transition: transform 0.3s ease;
    overflow: hidden;
  }

  .hover-slide:hover {
    transform: translateX(5px);
  }

  .loading-dots {
    animation: loadingDots 1.5s infinite;
  }

  .fade-in-stagger {
    animation: fadeInStagger 0.8s ease-out forwards;
    opacity: 0;
  }

  /* Water-themed premium animations */
  .water-ripple {
    position: relative;
    overflow: hidden;
  }

  .water-ripple::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: radial-gradient(circle, rgba(59, 130, 246, 0.3) 0%, transparent 70%);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: all 0.6s ease-out;
    pointer-events: none;
  }

  .water-ripple:hover::before {
    width: 300%;
    height: 300%;
  }

  .floating-particles {
    position: relative;
    overflow: hidden;
  }

  .floating-particles::after {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="20" cy="20" r="2" fill="rgba(59,130,246,0.3)"><animate attributeName="cy" values="20;80;20" dur="4s" repeatCount="indefinite"/></circle><circle cx="60" cy="60" r="1.5" fill="rgba(16,185,129,0.4)"><animate attributeName="cy" values="60;10;60" dur="3s" repeatCount="indefinite"/></circle><circle cx="80" cy="30" r="1" fill="rgba(245,158,11,0.3)"><animate attributeName="cy" values="30;90;30" dur="5s" repeatCount="indefinite"/></circle></svg>') repeat;
    animation: particleFloat 20s linear infinite;
    opacity: 0;
    transition: opacity 0.5s ease;
  }

  .floating-particles:hover::after {
    opacity: 1;
  }

  .text-reveal {
    display: inline-block;
    overflow: hidden;
  }

  .text-reveal-inner {
    display: inline-block;
    animation: textReveal 1.2s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
    transform: translateY(100%);
  }

  .gradient-shift {
    background: linear-gradient(45deg, #2563eb, #10b981, #f59e0b, #06b6d4);
    background-size: 400% 400%;
    animation: gradientShift 8s ease infinite;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .card-3d {
    transform-style: preserve-3d;
    transition: transform 0.6s cubic-bezier(0.23, 1, 0.320, 1);
    perspective: 1000px;
  }

  .card-3d:hover {
    transform: rotateY(15deg) rotateX(5deg) translateZ(20px);
  }

  .card-3d::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, transparent, rgba(59, 130, 246, 0.1), transparent);
    transform: translateX(-100%);
    transition: transform 0.6s ease;
  }

  .card-3d:hover::before {
    transform: translateX(100%);
  }

  .morphing-shadow {
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
    transition: box-shadow 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }

  .morphing-shadow:hover {
    box-shadow: 
      0 20px 60px rgba(59, 130, 246, 0.2),
      0 8px 30px rgba(16, 185, 129, 0.15),
      0 4px 15px rgba(0, 0, 0, 0.1);
  }

  .wave-background {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    position: relative;
    overflow: hidden;
  }

  .wave-background::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120"><path d="M321.39,56.44c58-10.79,114.16-30.13,172-41.86,82.39-16.72,168.19-17.73,250.45-.39C823.78,31,906.67,72,985.66,92.83c70.05,18.48,146.53,26.09,214.34,3V0H0V27.35A600.21,600.21,0,0,0,321.39,56.44Z" fill="rgba(255,255,255,0.1)"></path></svg>') repeat-x;
    animation: waveAnimation 15s ease-in-out infinite;
  }

  .breathing-scale {
    animation: breathingScale 4s ease-in-out infinite;
  }

  .cursor-glow {
    position: relative;
  }

  .cursor-glow::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    background: radial-gradient(circle, rgba(59, 130, 246, 0.4), transparent);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    opacity: 0;
    transition: all 0.3s ease;
    pointer-events: none;
  }

  .cursor-glow:hover::after {
    width: 100px;
    height: 100px;
    opacity: 1;
  }

  .parallax-element {
    transition: transform 0.1s ease-out;
  }

  .typing-effect {
    border-right: 2px solid #2563eb;
    animation: typing 3s steps(40) 1s 1 normal both, blinkCursor 0.8s infinite;
  }

  /* Staggered animation for service cards */
  .fade-in-stagger {
    animation: fadeInStagger 0.8s ease-out forwards;
    opacity: 0;
    transform: translateY(30px);
  }

  /* 3D card effect with proper depth */
  .card-3d {
    transition: all 0.6s cubic-bezier(0.23, 1, 0.32, 1);
  }

  .card-3d:hover {
    transform: perspective(1000px) rotateY(-8deg) rotateX(3deg) translateY(-10px);
  }

  /* Mobile-friendly hover states */
  @media (hover: none) and (pointer: coarse) {
    .card-3d:hover {
      transform: none;
    }
    .group:hover .opacity-0 {
      opacity: 1;
    }
    .group:hover .translate-y-4 {
      transform: translateY(0);
    }
  }

  /* Performance optimization classes */
  .transform-gpu {
    transform: translateZ(0);
    backface-visibility: hidden;
    will-change: transform;
  }

  /* Accessibility: Reduced motion preferences */
  @media (prefers-reduced-motion: reduce) {
    .fade-in-stagger,
    .animate-fade-in-up,
    .animate-slide-in-left,
    .animate-slide-in-right,
    .animate-zoom-in,
    .animate-float,
    .animate-pulse-slow,
    .animate-bounce-gentle,
    .animate-rotate-slow,
    .breathing-scale,
    .gradient-shift,
    .floating-particles::after {
      animation: none !important;
      opacity: 1 !important;
      transform: none !important;
    }

    .card-3d:hover {
      transform: translateY(-5px);
    }

    .hover-lift:hover {
      transform: translateY(-3px);
    }
  }
}

@keyframes fadeInUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes zoomIn {
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
}

@keyframes pulseGlow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(59, 130, 246, 0.4);
  }
  50% {
    box-shadow: 0 0 40px rgba(59, 130, 246, 0.8);
  }
}

@keyframes bounceGentle {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes rotateSlow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes slideUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes wiggle {
  0%, 7% {
    transform: rotateZ(0);
  }
  15% {
    transform: rotateZ(-15deg);
  }
  20% {
    transform: rotateZ(10deg);
  }
  25% {
    transform: rotateZ(-10deg);
  }
  30% {
    transform: rotateZ(6deg);
  }
  35% {
    transform: rotateZ(-4deg);
  }
  40%, 100% {
    transform: rotateZ(0);
  }
}

@keyframes loadingDots {
  0%, 80%, 100% {
    transform: scale(0);
  }
  40% {
    transform: scale(1);
  }
}

@keyframes fadeInStagger {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes textReveal {
  to {
    transform: translateY(0);
  }
}

@keyframes gradientShift {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

@keyframes particleFloat {
  0% {
    transform: translateX(-100%) translateY(0) rotate(0deg);
  }
  100% {
    transform: translateX(100%) translateY(-20px) rotate(360deg);
  }
}

@keyframes waveAnimation {
  0% {
    transform: translateX(0) translateY(0);
  }
  50% {
    transform: translateX(-25%) translateY(-10px);
  }
  100% {
    transform: translateX(-50%) translateY(0);
  }
}

@keyframes breathingScale {
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
}

@keyframes typing {
  0% {
    width: 0;
  }
  100% {
    width: 100%;
  }
}

@keyframes blinkCursor {
  0%, 50% {
    border-color: transparent;
  }
  51%, 100% {
    border-color: #2563eb;
  }
}

